mapper.insert:
		INSERT INTO MEMBER (
   								MBR_ID
   								, MBR_NAME
   								, BIRTHDAY
   								, PHONE
   								, SALT
   								, PASSWORD
   							) 
		VALUES 				( 	#{id}
								, #{name}
								, #{birthday}
								, #{phoneNumber}
								, #{salt}
								, #{password}
							)

mapper.select:
SELECT	SALT salt
FROM	MEMBER
WHERE	MBR_ID = #{id}
